                    }
                    
                    $n_c = 0;
                    foreach($result['popular_jobs'] as $rec){
                        $result['popular_jobs'][$n_c]['description'] = strip_tags($rec['description']);
                        $result['popular_jobs'][$n_c]['responsibilities'] = strip_tags($rec['responsibilities']);
